"use strict";function ys_setuptooltip(selector){var hoveritems=document.querySelectorAll(selector);hoveritems.forEach(function(hoveritem){let title=hoveritem.getAttribute("data-yspoptitle")||hoveritem.getAttribute("data-original-title")||hoveritem.getAttribute("title");let options=hoveritem.getAttribute("data-yspopoptions")||'{"mode" : "click, hover", "offset" : 20,"delay" : 20, "pos" : "top", "duration" : 200}';options=JSON.parse(options);options.container="#jevents";options.title=title;if(hoveritem.hasAttribute("title")){hoveritem.removeAttribute("title")}UIkit.tooltip(hoveritem,options)})}function ys_setuppopover(selector){var hoveritems=document.querySelectorAll(selector);hoveritems.forEach(function(hoveritem){let title=hoveritem.getAttribute("data-yspoptitle")||hoveritem.getAttribute("data-original-title")||hoveritem.getAttribute("title");let body=hoveritem.getAttribute("data-yspopcontent")||hoveritem.getAttribute("data-content")||hoveritem.getAttribute("data-bs-content")||"";let options=hoveritem.getAttribute("data-yspopoptions")||'{"mode" : "click, hover", "offset" : 10, "animation" : "", "delayHide" : 2000, "pos" : "left"}';options=JSON.parse(options);options.container="#jevents";let phtml='<div class="ys-popover-block">'+(title!=""?'<div class="ys-popover-title">'+title+"</div>":"")+(body!=""?'<div class="ys-popover-body">'+body+"</div>":"")+"</div>";options.title=phtml;if(hoveritem.hasAttribute("title")){hoveritem.removeAttribute("title")}UIkit.tooltip(hoveritem,options)})}document.addEventListener("DOMContentLoaded",function(){ys_setuppopover(".ys-popover")});document.addEventListener("DOMContentLoaded",function(){ys_setuptooltip(".ys-tooltip")});